mapper.select:
WITH StoreFoodTypes AS ( SELECT DISTINCT s.category AS store_category, FLOOR(MIN(ST_DistanceSphere(location, ST_GeomFromText(CONCAT('POINT(', #{currentLong}, ' ', #{currentLat}, ')'), 4326)))) AS distance FROM store s WHERE s.store_id IN
#{storeId}
GROUP BY s.category ), RankedFoodTypes AS ( SELECT ft.type1 AS category, ft.food_type_id, ft.represent_menu AS represent, ROW_NUMBER() OVER (PARTITION BY ft.type1 ORDER BY ft.food_type_id ASC) AS rn, ( SELECT distance FROM StoreFoodTypes WHERE store_category = ft.type2 LIMIT 1 ) AS distance FROM food_type ft WHERE ft.type2 IN (SELECT store_category FROM StoreFoodTypes) GROUP BY ft.type1, ft.type2, ft.food_type_id, ft.represent_menu ) SELECT category, food_type_id, represent, distance FROM RankedFoodTypes WHERE rn = 1 ORDER BY distance

SELECT s.store_id FROM store s JOIN food_type f ON s.category = f.type2 WHERE ST_DistanceSphere(location, ST_GeomFromText(CONCAT('POINT(', #{currentLong}, ' ', #{currentLat}, ')'), 4326)) <= 750 AND f.type1 IN ( SELECT DISTINCT type1 FROM food_type ft WHERE ft.food_type_id in
#{category}
)

SELECT s.store_id FROM store s JOIN food_type f ON s.category = f.type2 WHERE ST_DistanceSphere(location, ST_GeomFromText(CONCAT('POINT(', #{currentLong}, ' ', #{currentLat}, ')'), 4326)) <= 500 AND f.type1 IN ( SELECT DISTINCT type1 FROM food_type ft WHERE ft.food_type_id in
#{category}
)

SELECT s.store_id FROM store s JOIN food_type f ON s.category = f.type2 WHERE ST_DistanceSphere(location, ST_GeomFromText(CONCAT('POINT(', #{currentLong}, ' ', #{currentLat}, ')'), 4326)) <= 250 AND f.type1 IN ( SELECT DISTINCT type1 FROM food_type ft WHERE ft.food_type_id in
#{category}
)

SELECT s.store_id, s.name, s.address, ST_DistanceSphere(s.location, ST_GeomFromText(CONCAT('POINT(', #{currentLong}, ' ', #{currentLat}, ')'), 4326)) AS distance FROM store s WHERE s.store_id IN
#{storeId}
ORDER BY distance

SELECT b.broadcast_name, b.episode, b.broadcast_date, b.topic
FROM broadcast b
WHERE b.store_id = #{storeId}

WITH menu_counts AS ( SELECT store_id, COUNT(*) AS cnt FROM menu WHERE represent = 'Y' GROUP BY store_id ), menu_data AS ( SELECT m.store_id, m.menu_name, m.description, m.price, ROW_NUMBER() OVER (PARTITION BY m.store_id ORDER BY m.represent DESC, m.menu_id) AS rn FROM menu m ) SELECT s.store_id, s.name, s.address, ST_Y(s.location) as lat, ST_X(s.location) as lng, ST_DistanceSphere(s.location, ST_GeomFromText(CONCAT('POINT(', #{currentLong}, ' ', #{currentLat}, ')'), 4326)) AS original_distance, FLOOR(ST_DistanceSphere(s.location, ST_GeomFromText(CONCAT('POINT(', #{currentLong}, ' ', #{currentLat}, ')'), 4326))) AS distance, f.type1 AS category, f.food_type_id as category_id, CASE WHEN mc.cnt >= 3 THEN COALESCE(jsonb_agg( jsonb_build_object( 'menu_name', md.menu_name, 'description', md.description, 'price', md.price ) ) FILTER (WHERE md.rn <= 3), '[]'::jsonb) ELSE COALESCE(jsonb_agg( jsonb_build_object( 'menu_name', md.menu_name, 'description', md.description, 'price', md.price ) ) FILTER (WHERE md.rn <= 3), '[]'::jsonb) END AS menus, COALESCE(b.broadcasts, '[]'::jsonb) AS broadcasts FROM store s LEFT JOIN (SELECT st.store_id, f.type1, f.food_type_id FROM store st JOIN food_type f ON st.category = f.type2 WHERE st.store_id IN
#{storeId}
) f ON s.store_id = f.store_id LEFT JOIN menu_counts mc ON s.store_id = mc.store_id LEFT JOIN menu_data md ON s.store_id = md.store_id LEFT JOIN (SELECT b.store_id, jsonb_agg(b) AS broadcasts FROM broadcast b GROUP BY b.store_id) b ON s.store_id = b.store_id WHERE s.store_id in
#{storeId}
GROUP BY s.store_id, s.name, s.address, s.location, f.type1, mc.cnt, b.broadcasts, f.food_type_id ORDER BY original_distance
